document.addEventListener("DOMContentLoaded",function(){function e(){document.querySelectorAll(".article-category").forEach(e=>{const t=e.textContent.trim();e.setAttribute("data-category",t),"Nieuws"===t&&e.classList.add("nieuws-category")});document.querySelectorAll(".news-item").forEach(e=>{if(e.textContent.includes("Nieuws")){const t=e.querySelector(".news-item-icon");t&&t.classList.add("nieuws-icon")}})}e();new MutationObserver(function(t){t.forEach(function(t){t.addedNodes.length&&e()})}).observe(document.body,{childList:!0,subtree:!0})});